import React, { useContext } from 'react';
import './css/Shopcatagory.css';
import { ShopContext } from '../contexts/ShopContexts/Shopcontext';
import dropdown_icon from '../components/assets/dropdown_icon.png';
import Item from '../components/items/Item';
import ShopContextProvider from '../contexts/ShopContexts/Shopcontext'; // Import the ShopContextProvider

const ShopCategory = (props) => {
  return (
    <ShopContextProvider> {/* Wrap the component with ShopContextProvider */}
      <div className='shopcatagory'>
        <img src={props.banner} alt="" />
        <div className="shopcatagory-indexsort">
          <p>
            <span>
              showing 1-12
            </span>out of 36 Products
          </p>
          <div className="shopcatagory-sort">
            sort by <img src={dropdown_icon} alt="" />
          </div>
        </div>
        <ShopCategoryContent category={props.category} /> {/* Render ShopCategoryContent component */}
      </div>
    </ShopContextProvider>
  );
}

const ShopCategoryContent = ({ category }) => {
  const { all_product } = useContext(ShopContext);

  return (
    <div className="shopcatagory-product">
      {all_product.map((item, i) => {
        if (category === item.category) {
          return (
            <Item
              key={i}
              name={item.name}
              image={item.image}
              new_price={item.new_price}
              old_price={item.old_price}
            />
          );
        } else {
          return null;
        }
      })}
    </div>
  );
}

export default ShopCategory;
