import React, { useContext } from 'react';
import './css/Shopcatagory.css';
import { ShopContext } from '../contexts/ShopContext/ShopContext'; // Ensure this path matches the file name

const ShopCatagory = (props) => {
  const { all_product } = useContext(ShopContext);
  console.log(all_product); // Example usage to prevent 'never used' warning

  return (
    <div className='shopcatagory'>
      <img src={props.banner} alt="" />
      {/* Render products for demonstration */}
      {all_product.map((product, index) => (
        <div key={index}>
          <p>{product.name}</p>
          <img src={product.image} alt={product.name} />
        </div>
      ))}
    </div>
  );
}

export default ShopCatagory;
