import React from 'react';
import './Relatedproduct.css';
import data_product from '../assets/data';
import Item from '../items/Item';

const Relatedproduct = () => {
  // Log data_product to console for debugging
  console.log('data_product:', data_product);

  // Check if data_product is undefined or empty
  if (!data_product || data_product.length === 0) {
    return (
      <div className='relatedproduct'>
        <h1>Related Products</h1>
        <hr />
        <p>No related products found.</p>
      </div>
    );
  }

  // Render related products if data_product is valid
  return (
    <div className='relatedproduct'>
      <h1>Related Products</h1>
      <hr />
      <div className="relatedproduct-item">
        {data_product.map((item, i) => (
          <Item
            key={i}
            name={item.name}
            image={item.image}
            new_price={item.new_price}
            old_price={item.old_price}
          />
        ))}
      </div>
    </div>
  );
}

export default Relatedproduct;
