import React, { createContext, useEffect, useState } from "react";

export const ShopContext = createContext(null);

const getDefaultCart = () => {
  let cart = {};
  for (let index = 0; index < all_product.length; index++) { // 300+1
    cart[index] = 0;
  }
  return cart;
}

const ShopContextProvider = (props) => {
  const [all_product, setAll_product] = useState([]); // Ensure it's an array initially
  const [cartItems, setcartItems] = useState(getDefaultCart());

  useEffect(() => {
    fetch('http://localhost:4000/allproducts')
      .then((response) => response.json())
      .then((data) => {
        console.log('Fetched data:', data);
        if (Array.isArray(data)) {
          setAll_product(data);
        } else {
          console.error('Fetched data is not an array:', data);
        }
      })
      .catch((error) => console.error('Error fetching products:', error));
  }, []);

  const addtocart = (itemId) => {
    setcartItems((prev) => ({ ...prev, [itemId]: prev[itemId] + 1 }));
    console.log("Added to cart:", cartItems);
  }

  const removefromcart = (itemId) => {
    setcartItems((prev) => ({ ...prev, [itemId]: prev[itemId] - 1 }));
    console.log("Removed from cart:", cartItems);
  }

  const gettotalcartamount = () => {
    let totalamount = 0;
    for (const item in cartItems) {
      let iteminfo = all_product.find((product) => product.id === Number(item));
      if (iteminfo) {
        totalamount += iteminfo.new_price * cartItems[item];
      }
    }
    console.log("Total cart amount:", totalamount);
    return totalamount;
  }

  const gettotalcartitem = () => {
    let totalitem = 0;
    for (const item in cartItems) {
      if (cartItems[item] > 0) {
        totalitem += cartItems[item];
      }
    }
    return totalitem;
  }

  const contextValue = { all_product, cartItems, addtocart, removefromcart, gettotalcartamount, gettotalcartitem };

  return (
    <ShopContext.Provider value={contextValue}>
      {props.children}
    </ShopContext.Provider>
  );
};

export default ShopContextProvider;
